import React, { useState } from 'react';
import Header from '../components/Header';
import ControlsPanel from '../components/ControlsPanel';
import ResultsPanel from '../components/ResultsPanel';
import ChatWidget from '../components/ChatWidget';
import ModelInsights from '../components/ModelInsights';
import DetectionSummary from '../components/DetectionSummary'; // --- 1. IMPORT DetectionSummary ---
import { predictImage } from '../api/predict';

export default function Dashboard() {
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // This function is already correct and doesn't need changes.
  const handleDetect = async (file) => {
    if (!file) {
      setError("Please select a file first.");
      return;
    }

    setResult(null);
    setIsLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const data = await predictImage(formData);
      setResult(data);
    } catch (err) {
      setError(err.message || "Detection failed.");
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 font-sans">
      <Header />
      <main className="p-4 md:p-8 grid grid-cols-1 lg:grid-cols-3 gap-8 max-w-7xl mx-auto">
        
        {/* This column is correct */}
        <div className="lg:col-span-1 space-y-8">
          <ControlsPanel
            onDetect={handleDetect}
            isLoading={isLoading}
            error={error}
          />
          <ModelInsights />
        </div>
        
        {/* --- 2. UPDATE THIS COLUMN TO INCLUDE THE SUMMARIES --- */}
        <div className="lg:col-span-2 space-y-8">
          <ResultsPanel result={result} isLoading={isLoading} />
          
          {/* Show the Prediction Summary if a result exists */}
          {result && (
            <DetectionSummary 
              summary={result.prediction_summary} 
              title="Prediction Summary" 
            />
          )}

          {/* Conditionally show the Ground Truth Summary ONLY if it exists in the API response */}
          {result && result.ground_truth_summary && (
            <DetectionSummary 
              summary={result.ground_truth_summary} 
              title="Ground Truth Summary" 
            />
          )}
        </div>

      </main>
      <ChatWidget />
    </div>
  );
}
